#include <iostream>
#include <vector>
#include <string>
#include <algorithm>

using namespace std;

class Solution {
public:
  vector<int> findSubstring(string S, vector<string> &L) {
    vector<int> sol;
    if (L.size() == 0 || (L[0].size() > S.size())) return sol;

    wordLength = L[0].size();
    numWords = L.size();
    stringLength = wordLength*numWords;
    stringLengthLeft = stringLength+wordLength;
    stringLengthRight = stringLength-wordLength;
    int leftEdge = 0;
    sSize = S.size()-1;
    int rightEdge = sSize-wordLength;
    
    // std::cout << "rightEdge = " << rightEdge << std::endl;
    
    for (int i=0; i<S.size()-wordLength; ++i) {
      if (S.substr(i, wordLength) == L[0]) {
        leftEdge = std::max(0, i-stringLengthLeft);
        // std::cout << "new = min(" << rightEdge << ", " << i+stringLength-wordLength << std::endl;
        rightEdge = std::min(sSize-wordLength, i+stringLengthRight);
        // std::cout << "match at position: " << i << ", " << rightEdge << std::endl;
        std::pair<int,int> p(checkString(S, L, i, 1, leftEdge, rightEdge));
        if (p.first != -1) {
          sol.push_back(p.first);
        }
      }
    }
    
    return sol;
  }
  
  std::pair<int,int> checkString(const string & S, const vector<string> & L,
                                 int pos, int index, int leftEdge, int rightEdge)
  {
    // std::cout << "pos = " << pos << ", index = " << index << std::endl;
    if (index > numWords-1) return std::make_pair(leftEdge, rightEdge);
    // std::cout << "pos = " << pos << ", index = " << index << std::endl;
    int position = pos-wordLength;
    while (position >= leftEdge) {
      // std::cout << "position = " << position << std::endl;
      if (S.substr(position, wordLength) == L[index]) {
        std::pair<int,int> p(checkString(S, L, pos, index+1,
                                         std::max(std::max(0, position-stringLengthLeft), leftEdge),
                                         std::min(std::min(sSize, position+stringLengthRight), rightEdge)));
        // std::cout << "backward p.first = " << p.first << std::endl;
        if (p.first != -1) return p;
      }
      position -= wordLength;
    }
    // Very ugly
    position = pos+wordLength;
    // std::cout << "position = " << position << " rightEdge = " << rightEdge << std::endl;
    // std::cout << "L["<<index<<"] = " << L[index] << ", " << S.substr(position, wordLength) << std::endl;
    while (position <= rightEdge) {
      if (S.substr(position, wordLength) == L[index]) {
        // std::cout << "position = " << position << std::endl;
        leftEdge = std::max(std::max(0, position-stringLengthLeft), leftEdge);
        rightEdge = std::min(std::min(sSize, position+stringLengthRight), rightEdge);
        std::pair<int,int> p(checkString(S, L, pos, index+1, leftEdge, rightEdge));
        // std::cout << "forward p.first = " << p.first << std::endl;
        if (p.first != -1) return p;
      }
      position += wordLength;
    }
    return std::make_pair(-1,-1);
  }
  
  int numWords;
  int sSize;
  int wordLength;
  int stringLength;
  int stringLengthLeft;
  int stringLengthRight;
};

